(()=>{console.log("This site was generated by Hugo.");document.addEventListener("DOMContentLoaded",t);function t(){let e=document.getElementById("theme-btn");window.localStorage.getItem("theme","light")==="light"?e.innerHTML=`
		[Dark/<span>Light</span>]
		`:e.innerHTML=`
		[<span>Dark</span>/Light]
		`,e.addEventListener("click",()=>{window.localStorage.getItem("theme","light")==="light"?(document.documentElement.classList.add("dark"),window.localStorage.setItem("theme","dark"),e.innerHTML=`
			[<span>Dark</span>/Light]
			`):(document.documentElement.classList.remove("dark"),window.localStorage.setItem("theme","light"),e.innerHTML=`
			[Dark/<span>Light</span>]
			`)})}})();
